import React, { Component } from 'react'
import { connect } from 'react-redux'

export class BlogForm extends Component {

  componentDidMount(){

  }   
  render() {
    return (
     <div className="ui segment">

      <div className="ui form">

      <div className="field">

            <label>
                Post Title
            </label>

            <input type="text">
            </input>

      </div>

      <div className="field">
            <label>Description</label>
            <textarea placeholder="enter a post body"></textarea>
       </div>

        <div className="field">
            <input className="ui primary button"  type="button" value="Add Post"/>
        </div>
      </div>
      </div>   
    )
  }
}

const mapStateToProps = state => {
    blogs : state.blogs
}

export default connect(mapStateToProps)(BlogForm)
