import { FETCH_BLOGS,ADD_BLOG } from '../actions/types';


const initialState = {
    blogs : [],
    blog : {}
}

export default function(state = initialState,action){

    switch(action.type){
        case FETCH_BLOGS:
            console.log("reducer",action.payload);    
            return{
                ...state,
                blogs : action.payload
            };
        case ADD_BLOG:
            return{
                ...state,
                blog: state.blogs.concat([action.payload])
            }    
        default:
            return state;    
    }
}
